@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800;900&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  html {
    font-family: 'Inter', system-ui, sans-serif;
    font-feature-settings: 'cv02', 'cv03', 'cv04', 'cv11';
  }

  body {
    @apply bg-gradient-to-br from-indigo-50 via-white to-cyan-50 min-h-screen;
    background-attachment: fixed;
  }

  * {
    @apply scroll-smooth;
  }

  /* Custom properties for dynamic theming */
  :root {
    --primary-50: #eff6ff;
    --primary-100: #dbeafe;
    --primary-500: #3b82f6;
    --primary-600: #2563eb;
    --primary-700: #1d4ed8;
    --primary-900: #1e3a8a;

    --gradient-primary: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    --gradient-secondary: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
    --gradient-success: linear-gradient(135deg, #4facfe 0%, #00f2fe 100%);
    --gradient-warning: linear-gradient(135deg, #43e97b 0%, #38f9d7 100%);

    --shadow-premium: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
    --shadow-card: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
    --shadow-card-hover: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
  }
}

@layer components {
  /* Premium Button System */
  .btn {
    @apply inline-flex items-center justify-center px-6 py-3 text-sm font-semibold rounded-xl border transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed transform hover:scale-[1.02] active:scale-[0.98];
  }

  .btn-primary {
    @apply btn text-white border-transparent shadow-lg hover:shadow-xl;
    background: var(--gradient-primary);
    box-shadow: 0 4px 14px 0 rgba(102, 126, 234, 0.4);
  }

  .btn-primary:hover {
    box-shadow: 0 6px 20px 0 rgba(102, 126, 234, 0.6);
  }

  .btn-secondary {
    @apply btn bg-white text-slate-700 border-slate-200 hover:bg-slate-50 focus:ring-slate-500;
  }

  .btn-ghost {
    @apply btn bg-transparent text-slate-700 border-transparent hover:bg-slate-100 focus:ring-slate-500;
  }

  .btn-success {
    @apply btn bg-success-600 text-white border-transparent hover:bg-success-700 focus:ring-success-500;
  }

  .btn-warning {
    @apply btn bg-warning-600 text-white border-transparent hover:bg-warning-700 focus:ring-warning-500;
  }

  /* Premium Card System */
  .card {
    @apply bg-white/80 backdrop-blur-xl rounded-2xl border border-white/20 transition-all duration-300 hover:shadow-xl;
    box-shadow: var(--shadow-card);
  }

  .card:hover {
    box-shadow: var(--shadow-card-hover);
    transform: translateY(-4px);
  }

  .card-glass {
    @apply backdrop-blur-2xl rounded-2xl border border-white/30 transition-all duration-300;
    background: rgba(255, 255, 255, 0.1);
    box-shadow: 0 8px 32px 0 rgba(31, 38, 135, 0.37);
  }

  .card-header {
    @apply px-8 py-6 border-b border-slate-200/50;
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.1), rgba(255, 255, 255, 0.05));
  }

  .card-body {
    @apply px-8 py-6;
  }

  .card-footer {
    @apply px-8 py-6 border-t border-slate-200/50 bg-gradient-to-r from-slate-50/50 to-slate-100/50 rounded-b-2xl;
  }

  /* Premium Input System */
  .input {
    @apply block w-full px-4 py-3 border border-slate-200 rounded-xl text-sm placeholder:text-slate-400 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 transition-all duration-300 bg-white/70 backdrop-blur-sm hover:bg-white focus:bg-white;
  }

  .input:focus {
    box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1), 0 4px 6px -1px rgba(0, 0, 0, 0.1);
  }

  .input-error {
    @apply input border-red-300 focus:ring-red-500 focus:border-red-500;
  }

  /* Status System */
  .badge {
    @apply inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium;
  }

  .badge-success {
    @apply badge bg-success-100 text-success-800;
  }

  .badge-warning {
    @apply badge bg-warning-100 text-warning-800;
  }

  .badge-error {
    @apply badge bg-red-100 text-red-800;
  }

  .badge-info {
    @apply badge bg-primary-100 text-primary-800;
  }

  .badge-neutral {
    @apply badge bg-slate-100 text-slate-800;
  }

  /* Chat System */
  .message-bubble {
    @apply max-w-sm px-4 py-2 rounded-lg text-sm;
  }

  .message-user {
    @apply message-bubble bg-primary-600 text-white ml-auto;
  }

  .message-bot {
    @apply message-bubble bg-white border border-slate-200 text-slate-900;
  }

  /* Page Templates */
  .page-header {
    @apply flex items-center justify-between pb-6 border-b border-slate-200;
  }

  .page-title {
    @apply text-2xl font-bold text-slate-900;
  }

  .section-title {
    @apply text-lg font-semibold text-slate-900 mb-4;
  }

  /* Table System */
  .table {
    @apply w-full text-sm text-left;
  }

  .table-header {
    @apply bg-slate-50 border-b border-slate-200;
  }

  .table-header th {
    @apply px-4 py-3 text-xs font-semibold text-slate-500 uppercase tracking-wider;
  }

  .table-row {
    @apply border-b border-slate-100;
  }

  .table-row:hover {
    @apply bg-slate-50;
  }

  .table-cell {
    @apply px-4 py-3 text-slate-900;
  }

  /* Loading States */
  .skeleton {
    @apply animate-pulse bg-slate-200 rounded;
  }
}

@keyframes shimmer {
  0% { background-position: -200% 0; }
  100% { background-position: 200% 0; }
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}

@keyframes pulse-glow {
  0%, 100% { box-shadow: 0 0 5px rgba(59, 130, 246, 0.4); }
  50% { box-shadow: 0 0 20px rgba(59, 130, 246, 0.6), 0 0 30px rgba(59, 130, 246, 0.4); }
}

@keyframes gradient-shift {
  0%, 100% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
}

/* Utility classes for premium effects */
.animate-float {
  animation: float 3s ease-in-out infinite;
}

.animate-pulse-glow {
  animation: pulse-glow 2s ease-in-out infinite;
}

.animate-gradient {
  background-size: 200% 200%;
  animation: gradient-shift 3s ease infinite;
}

.glass-effect {
  backdrop-filter: blur(16px) saturate(180%);
  background-color: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.125);
}

.premium-gradient {
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
}

.success-gradient {
  background: var(--gradient-success);
}

.warning-gradient {
  background: var(--gradient-warning);
}

.premium-shadow {
  box-shadow: var(--shadow-premium);
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 6px;
}

::-webkit-scrollbar-track {
  background: rgba(148, 163, 184, 0.1);
  border-radius: 3px;
}

::-webkit-scrollbar-thumb {
  background: rgba(148, 163, 184, 0.4);
  border-radius: 3px;
}

::-webkit-scrollbar-thumb:hover {
  background: rgba(148, 163, 184, 0.6);
}

